import React, {Component} from 'react'

class AddTodo extends Component {
    state = {
        content:""
    }
    handleSubmit = (e) => {
        e.preventDefault();

    } 
    render(){
        return (
            <div className="add-form">
                <form onSubmit={this.handleSubmit}>
                    <label>Add new Todo</label>
                    <Input type="text" ></Input>
                </form>
            </div>
        )
    }
}
export default AddTodo;
